import React from "react";

function Button({ children, onClick, type, addClassNames }) {
  let color = "sky";
  if (type === "error") color = "red";
  return (
    <button
      onClick={onClick}
      className={`bg-${color}-500 hover:bg-sky-700 focus:outline-none py-3 px-6 text-white shadow rounded ${addClassNames}`}
    >
      {children}
    </button>
  );
}

export default Button;
